public class MaxHeapify {

	/**
	 * @param args
	 */
	
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int noofrows = 5;
		int noofcols = noofrows-1;
		
	for(int j=1;j<noofrows;j++) {	
		for(int i=1;i<noofcols;i++) {
			
			System.out.print("-");
			
		
			
		}	noofcols--;}
	
	     for(int k=0;k<noofrows;k++) {
	    	 
	    	 System.out.print("*");
	    	 
	     }
	
			System.out.print("\n");
		}
		
	}


